package problems;

public class problem7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Task 7- Write a program to print below pattern
		
		for(int i =1 ; i <= 6 ; i++)
		{
			System.out.println("");
		for(int j =1 ; j <= i ; j++)
		{
			System.out.print("*");
		}
			
		}

	}

}
